import React, { useState, useMemo } from 'react';
import Filters from '../components/Filters';
import ProductList from '../components/ProductList';

/**
 * The Home page component, which orchestrates the filters and product list.
 * @param {object} props
 * @param {Array} props.products - The full list of products.
 */
function Home({ products }) {
  // State for the selected category filter
  const [selectedCategory, setSelectedCategory] = useState('all');

  // Memoize the list of unique categories
  const categories = useMemo(() => {
    // Get all unique categories from the products
    const allCategories = products.map(p => p.category);
    return ['all', ...new Set(allCategories)];
  }, [products]);

  // Memoize the filtered list of products
  const filteredProducts = useMemo(() => {
    if (selectedCategory === 'all') {
      return products;
    }
    return products.filter(p => p.category === selectedCategory);
  }, [products, selectedCategory]);

  return (
    <div className="home-container">
      <div className="page-header">
        <h1>DISCOVER OUR PRODUCTS</h1>
        <p>Lorem ipsum dolor sit amet consectetur. Amet est posuere rhoncus scelerisque. Dolor integer scelerisque nibh amet mi ut elementum dolor.</p>
      </div>

      <div className="filter-bar">
        <div className="filter-controls">
          <span>{filteredProducts.length} ITEMS</span>
          <button className="filter-toggle" onClick={() => {
            // This is a placeholder for mobile filter toggle logic
            const filterEl = document.querySelector('.filters-sidebar');
            if (filterEl) filterEl.classList.toggle('open');
          }}>
            <i className="fa-solid fa-arrow-left"></i> HIDE FILTER
          </button>
        </div>
        <div className="sort-controls">
          <select>
            <option value="recommended">RECOMMENDED</option>
            <option value="price-asc">Price: Low to High</option>
            <option value="price-desc">Price: High to Low</option>
          </select>
        </div>
      </div>

      <main className="main-content">
        <Filters 
          categories={categories}
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
        />
        <ProductList products={filteredProducts} />
      </main>
    </div>
  );
}

export default Home;

