import React from 'react';

/**
 * A single product card.
 * @param {object} props
 * @param {object} props.product - The product data to display.
 */
function ProductCard({ product }) {
  const shortDescription = product.description.substring(0, 100) + '...';

  // SEO JSON-LD Schema
  const schema = {
    "@context": "https://schema.org/",
    "@type": "Product",
    "name": product.title,
    "image": product.image,
    "description": product.description,
    "brand": {
      "@type": "Brand",
      "name": "FakeStore"
    },
    "sku": product.id,
    "offers": {
      "@type": "Offer",
      "url": "https://example.com/product/" + product.id,
      "priceCurrency": "USD",
      "price": product.price,
      "availability": "https://schema.org/InStock",
      "itemCondition": "https://schema.org/NewCondition"
    }
  };

  return (
    <article className="product-card">
      {/*  Use dangerouslySetInnerHTML for SSR compatibility */}
      <script
        type="application/ld+json"
        dangerouslySetInnerHTML={{ __html: JSON.stringify(schema) }}
      />

      <div className="product-image-container">
        <img
          src={product.image}
          alt={product.title}
          className="product-image"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = "https://placehold.co/300x400?text=Image+Not+Found";
          }}
        />
        <button className="add-to-cart-btn">ADD TO CART</button>
      </div>

      <div className="product-info">
        <h2 className="product-title">{product.title}</h2>
        <div className="product-meta">
          <p className="product-description">{shortDescription}</p>
          <button className="wishlist-btn">
            <i className="fa-regular fa-heart"></i>
          </button>
        </div>
        <p className="product-price">${product.price.toFixed(2)}</p>
      </div>
    </article>
  );
}

export default ProductCard;

